mapper.select:
select articleno, subject, userid, content, hit, regtime
from board
where articleno = #{articleno}

mapper.insert:
insert into board(
subject, userid, content
) values (
#{subject}, #{userid}, #{content}
)

mapper.update:
update board
set subject = #{subject},
userid = #{userid},
content = #{content}
where articleno = #{articleno}

mapper.select:
select articleno, userid, subject, content, hit, regtime
from board
where articleno = #{articleno}

mapper.update:
update board
set hit = hit + 1
where articleno = #{articleno}
